mapper.select:
SELECT d.*, req.emp_name req_name, appr.emp_name appr_name FROM documents d
    LEFT JOIN human_resource req ON req.emp_id = d.req_id
    LEFT JOIN human_resource appr ON appr.emp_id   = d.appr_id


        AND cat_id = #{cat_id}


        AND tb_id = #{tb_id}


        AND cd_id = #{cd_id}


        AND req_id = #{req_id}


        AND DATE(req_date) = #{req_date}


        AND appr_id = #{appr_id}


        AND DATE(appr_date) = #{appr_date}


        AND d.status = #{status}


        AND (d.req_id = #{member_id} OR d.appr_id = #{member_id})
