mapper.select:
SELECT *
FROM unpaid
WHERE cat_id = #{cat_id}
  AND tb_id  = #{tb_id}
  AND ref_pk = #{ref_pk}

SELECT *
FROM unpaid


        AND cat_id = #{cat_id}


        AND tb_id = #{tb_id}


        AND type = #{type}


        AND status = #{status}

mapper.update:
UPDATE unpaid
SET
    status    = 1,
    paid_date = NOW()
WHERE cat_id = #{cat_id}
  AND tb_id  = #{tb_id}
  AND ref_pk = #{ref_pk}

UPDATE unpaid
SET
    status      = 2,
    cost        = 0,
    paid_date   = NOW()
WHERE cat_id = #{cat_id}
  AND tb_id  = #{tb_id}
  AND ref_pk = #{ref_pk}
