mapper.select:
SELECT outbound_id, outbound_date, stock_id, outbound_qty, ac_id, emp_id, remark, approval
FROM outbound



        AND DATE(outbound_date) = DATE(#{outbound_date})




        AND stock_id = #{stock_id}


        AND ac_id = #{ac_id}


        AND emp_id = #{emp_id}


        AND approval = #{approval}

mapper.update:
UPDATE outbound
outbound_date = #{outbound_date}, stock_id = #{stock_id}, outbound_qty = #{outbound_qty}, ac_id = #{ac_id}, emp_id = #{emp_id}, remark = #{remark}, approval = #{approval},
WHERE outbound_id = #{outbound_id}
