mapper.select:
SELECT stock_id, stock_name, qty, unit_price, location, type
FROM stock


        AND stock_name LIKE CONCAT('%', #{stock_name}, '%')


        AND qty = #{qty}


        AND unit_price = #{unit_price}


        AND location LIKE CONCAT('%', #{location}, '%')


        AND type = #{type}


        AND is_deleted = #{is_deleted}

mapper.update:
UPDATE stock
stock_name = #{stock_name}, qty = #{qty}, unit_price = #{unit_price}, location = #{location}, type = #{type},
WHERE stock_id = #{stock_id}
