mapper.select:
SELECT s.*, a.ac_name AS ac_name
FROM stock s
LEFT JOIN accounts a
ON a.ac_id = s.ac_id


        AND s.stock_name LIKE CONCAT('%', #{stock_name}, '%')


        AND s.qty = #{qty}


        AND s.unit_price = #{unit_price}


        AND s.location LIKE CONCAT('%', #{location}, '%')


        AND s.type = #{type}

    AND s.is_deleted = 0

mapper.update:
UPDATE stock
stock_name = #{stock_name}, qty = #{qty}, unit_price = #{unit_price}, acquired_qty = #{acquired_qty}, location = #{location}, type = #{type}, ac_id = #{ac_id},
WHERE stock_id = #{stock_id}
