mapper.select:
SELECT w.work_order_id, w.stock_id, s.stock_name, w.emp_id, hr.emp_name, w.start_date, w.due_date, w.target_qty, w.good_qty, w.defect_qty, w.request_date, CASE WHEN w.order_status = 0 THEN '대기' WHEN w.order_status = 1 THEN '진행중' WHEN w.order_status = 2 THEN '완료' WHEN w.order_status = 99 THEN '승인대기' ELSE '알 수 없음' END AS order_status FROM work_order w LEFT JOIN stock s ON w.stock_id = s.stock_id LEFT JOIN human_resource hr ON w.emp_id = hr.emp_id
AND w.order_status != 99
AND w.order_status = 0 AND w.order_status = 1 AND w.order_status = 2
AND s.stock_name LIKE CONCAT('%', #{stock_name}, '%')
AND w.start_date >= #{start_date}
AND w.due_date <= #{due_date}

mapper.update:
UPDATE work_order
SET order_status = #{status}
WHERE work_order_id = #{work_order_id}

mapper.insert:
INSERT INTO work_order (
    stock_id, emp_id, start_date, due_date,
    target_qty, request_date,
    good_qty, defect_qty, order_status
)
VALUES (
           #{stock_id}, #{emp_id}, #{start_date}, #{due_date},
           #{target_qty}, #{request_date},
           0, 0, #{order_status}
       )

mapper.update:
UPDATE bom
SET required_qty = #{required_qty}
WHERE bom_id = #{bom_id}

UPDATE work_order
SET
good_qty = #{good_qty},
defect_qty = #{defect_qty},  order_status = #{order_status}

    , due_date = CURDATE()

WHERE work_order_id = #{work_order_id}
