mapper.select:
SELECT
    p.*,
    a.ac_name,
    hr.emp_name
FROM purchase p
LEFT JOIN human_resource hr ON hr.emp_id = p.emp_id
LEFT JOIN accounts a ON a.ac_id   = p.ac_id


        AND p.purchase_id = #{purchase_id}


        AND p.emp_id = #{emp_id}


        AND p.ac_id = #{ac_id}


        AND p.order_date = #{order_date}


        AND p.del_date = #{del_date}


        AND p.status = #{status}

mapper.update:
UPDATE purchase
SET emp_id = #{emp_id}, ac_id = #{ac_id}, order_date = #{order_date}, del_date = #{del_date}, total_price = #{total_price}
WHERE purchase_id = #{purchase_id}

UPDATE purchase
SET  status = #{status}
WHERE purchase_id = #{purchase_id}
