mapper.select:
SELECT
    s.*,
    a.ac_name,
    hr.emp_name
FROM sale s
LEFT JOIN human_resource hr ON hr.emp_id = s.emp_id
LEFT JOIN accounts a ON a.ac_id   = s.ac_id


        AND s.sale_id = #{sale_id}


        AND s.emp_id = #{emp_id}


        AND s.ac_id = #{ac_id}


        AND s.order_date = #{order_date}


        AND s.due_date = #{due_date}


        AND s.status = #{status}

    AND s.status != 99

mapper.update:
UPDATE sale
SET emp_id = #{emp_id}, ac_id = #{ac_id}, order_date = #{order_date}, due_date
    = #{due_date}, total_price = #{total_price}
WHERE sale_id = #{sale_id}

UPDATE sale
SET  status = #{status}
WHERE sale_id = #{sale_id}
